import React, { PureComponent } from 'react'
import NewsItem from './NewsItem'

export class News extends PureComponent {
  render() {
    return (
      <div className='container my-3'>
          <h2><center>News App - Top Headlines</center></h2>
          <div className='row'>
              <div className='col-md-4'>  {/* BootStrap has 12 column grid */}
              <NewsItem title="Global News" description="All the world news" imageUrl=""/>
              </div>
              <div className='col-md-4'> {/* col-md-4 means 1 div will take 4 column space */}
              <NewsItem title="Global News" description="All the world news" imageUrl=""/>
              </div>
              <div className='col-md-4'>
              <NewsItem title="Global News" description="All the world news" imageUrl=""/>
              </div>
          </div>
      </div>

    )
  }
}

export default News
